		url += '&filter_date_added=' + encodeURIComponent(filter_date_added);
	}

	var filter_date_modified = '';	
	if (filter_date_modified) {
		url += '&filter_date_modified=' + encodeURIComponent(filter_date_modified);
	}

	location = url;
}

